var relacionados={
	posts:[],
	max:4,
	maxLen:100,
	imgPorDefecto:"http://emiliocobos.site40.net/blogger/images/blogger1.png",
	plantilla:'<div class="relsposts"><a target="_blank" rel="nofollow" title="{{titulo}}" href="{{url}}"><img src="{{img}}" alt="{{titulo}}"/></a><h6><a target="_blank" rel="nofollow" title="{{titulo}}" href="{{url}}">{{titulo}}</a></h6><p>{{resumen}}</p></div>'
},postRepetido=function(a,b){var c=0;for(;a[c];c++){if(a[c].url===b.url){return true}}return false};
function eliminattags(a,b){var c=a.split("<");for(var d=0;d<c.length;d++){if(c[d].indexOf(">")!=-1){c[d]=c[d].substring(c[d].indexOf(">")+1,c[d].length)}}c=c.join("");return c.substring(0,b-1)+"..."}function mostrarrelacionados(a){var b=relacionados.posts,c=Math.floor(Math.random()*b.length),d=b[c],e=0,f="",g;if(c===b.length-1){c=0}else{c++}for(;b[c];c++){g=b[c];f+=relacionados.plantilla.replace(/{{titulo}}/g,g.tit).replace(/{{url}}/g,g.url).replace(/{{img}}/g,g.img).replace(/{{resumen}}/g,g.contenido);if(g===d){break}if(++e===relacionados.max){break}if(c===b.length-1){c=-1}}if(a){a=typeof a==="string"?document.getElementById(a):a;a.innerHTML=f}else{document.write(f)}}function leerpostetiquetas(a){var b=a.feed.entry,c=0,d,e,f,g;for(;b[c];c++){d=0;g={};e=b[c];f=e.link;g.tit=e.title.$t;contenido=e.content||e.summary;g.contenido=eliminattags(contenido.$t,relacionados.maxLen);g.img=e.media$thumbnail?e.media$thumbnail.url:relacionados.imgPorDefecto||"";for(;f[d];d++){if(f[d].rel==="alternate"){g.url=f[d].href;break}}if(!postRepetido(relacionados.posts,g)&&g.url!==document.URL){relacionados.posts.push(g)}}}
